import React, { useCallback, useEffect, useState } from 'react'
import Quill from "quill"
import "quill/dist/quill.snow.css"
import { io } from 'socket.io-client'

const TOOLBAR_COMMANDS = [
    [{ header: [1, 2, 3, 4, 5, 6, false] }],
    [{ font: [] }],
    [{ script: "sub" }, { script: "super" }],
    ["bold", "italic", "underline"],
    [{ list: "ordered" }, { list: "bullet" }],
    [{ color: [] }, { background: [] }],
    [{ align: [] }],
    ["image", "blockquote", "code-block"],
    ["clean"],
  ]

export default function TextEditor() {
  const [socket, setSocket] = useState()
  const [quill, setSQuill] = useState()
  useEffect(() => {
    const s = io("https://localhost:3001")
    setSocket(s)

    return () => {
      socket.disconnect()
    }
  }, [])

  useEffect(() => {
    if (socket == null || quill == null) return 

    const handler = quill.on('text-change', (delta, oldDelta, source) => {
      if (source !== 'user') return 
      socket.emit("send-changes", delta)
    })

    return () => {
      quill.off('text-change', )
    }
  }, [socket, quill])

    const stop_many_toolbars = useCallback((wrapper) => {
        if (wrapper == null) return
        wrapper.innerHTML = ""
        const editor = document.createElement("div")
        wrapper.append(editor)
        const q = new Quill(editor, {theme: "snow", modules: { toolbar: TOOLBAR_COMMANDS}})
    }, [])
    setSQuill(q)
    return (
    <div className="paper" ref={stop_many_toolbars}></div>
  )
}
